mapper.update:
		update book 
    		set rperson = #{param2},
        		name = #{param3},
				mobile = #{param4}
    		where bookcode = #{param1}

mapper.select:
select a.roomcode, a.name as roomname, b.name as typename, a.howmany, a.howmuch from room a, roomtype b where a.type=b.typecode and a.roomcode in ( select roomcode from room minus select distinct roomcode from book
where (checkout >= #{param1} and checkin <= #{param2})
)
